import { Platform } from 'react-native';

// AWS Database Configuration
const AWS_CONFIG = {
  region: 'YOUR_AWS_REGION', // e.g., 'us-east-1'
  credentials: {
    accessKeyId: 'YOUR_ACCESS_KEY_ID',
    secretAccessKey: 'YOUR_SECRET_ACCESS_KEY',
  },
  endpoint: 'YOUR_AWS_DATABASE_ENDPOINT', // e.g., 'your-db.xxxxx.region.rds.amazonaws.com'
  port: 5432, // Default PostgreSQL port
  database: 'YOUR_DATABASE_NAME',
};

// For web, we'll use a REST API endpoint
const API_URL = 'YOUR_API_GATEWAY_URL'; // Your AWS API Gateway URL

// For native platforms, we'll use SQLite
const SQLite = Platform.OS !== 'web' ? require('react-native-sqlite-storage') : null;

class DatabaseService {
  constructor() {
    if (Platform.OS !== 'web' && SQLite) {
      this.db = SQLite.openDatabase({
        name: 'streamflix.db',
        location: 'default',
      });
      this.initDatabase();
    }
  }

  initDatabase() {
    if (Platform.OS === 'web') return;

    this.db.transaction(tx => {
      // Create users table
      tx.executeSql(
        'CREATE TABLE IF NOT EXISTS users (id INTEGER PRIMARY KEY AUTOINCREMENT, email TEXT UNIQUE, password TEXT, preferences TEXT)',
        [],
        () => console.log('Users table created'),
        (_, error) => console.error('Error creating users table:', error)
      );

      // Create watchlist table
      tx.executeSql(
        'CREATE TABLE IF NOT EXISTS watchlist (id INTEGER PRIMARY KEY AUTOINCREMENT, user_id INTEGER, movie_id INTEGER, title TEXT, image TEXT, description TEXT, rating REAL, FOREIGN KEY (user_id) REFERENCES users (id))',
        [],
        () => console.log('Watchlist table created'),
        (_, error) => console.error('Error creating watchlist table:', error)
      );

      // Create user preferences table
      tx.executeSql(
        'CREATE TABLE IF NOT EXISTS user_preferences (id INTEGER PRIMARY KEY AUTOINCREMENT, user_id INTEGER, movie_id INTEGER, title TEXT, image TEXT, description TEXT, rating REAL, FOREIGN KEY (user_id) REFERENCES users (id))',
        [],
        () => console.log('User preferences table created'),
        (_, error) => console.error('Error creating user preferences table:', error)
      );
    });
  }

  // Helper function to get database connection
  async getConnection() {
    if (Platform.OS === 'web') {
      return null;
    }

    const { Client } = require('pg');
    const client = new Client({
      host: AWS_CONFIG.endpoint,
      port: AWS_CONFIG.port,
      database: AWS_CONFIG.database,
      user: AWS_CONFIG.credentials.accessKeyId,
      password: AWS_CONFIG.credentials.secretAccessKey,
      ssl: {
        rejectUnauthorized: false // Required for AWS RDS
      }
    });

    await client.connect();
    return client;
  }

  // User operations
  async createUser(email, password) {
    if (Platform.OS === 'web') {
      const response = await fetch(`${API_URL}/users`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });
      return response.json();
    }

    try {
      const client = await this.getConnection();
      const result = await client.query(
        'INSERT INTO users (email, password) VALUES ($1, $2) RETURNING id',
        [email, password]
      );
      await client.end();
      return result.rows[0].id;
    } catch (error) {
      console.error('Error creating user:', error);
      throw error;
    }
  }

  async getUser(email, password) {
    if (Platform.OS === 'web') {
      const response = await fetch(`${API_URL}/users/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });
      return response.json();
    }

    try {
      const client = await this.getConnection();
      const result = await client.query(
        'SELECT * FROM users WHERE email = $1 AND password = $2',
        [email, password]
      );
      await client.end();
      return result.rows[0];
    } catch (error) {
      console.error('Error getting user:', error);
      throw error;
    }
  }

  // Watchlist operations
  async addToWatchlist(userId, movie) {
    if (Platform.OS === 'web') {
      const response = await fetch(`${API_URL}/watchlist`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, movie }),
      });
      return response.json();
    }

    try {
      const client = await this.getConnection();
      const result = await client.query(
        'INSERT INTO watchlist (user_id, movie_id, title, image, description, rating) VALUES ($1, $2, $3, $4, $5, $6) RETURNING id',
        [userId, movie.id, movie.title, movie.image, movie.description, movie.rating]
      );
      await client.end();
      return result.rows[0].id;
    } catch (error) {
      console.error('Error adding to watchlist:', error);
      throw error;
    }
  }

  async getWatchlist(userId) {
    if (Platform.OS === 'web') {
      const response = await fetch(`${API_URL}/watchlist/${userId}`);
      return response.json();
    }

    try {
      const client = await this.getConnection();
      const result = await client.query(
        'SELECT * FROM watchlist WHERE user_id = $1',
        [userId]
      );
      await client.end();
      return result.rows;
    } catch (error) {
      console.error('Error getting watchlist:', error);
      throw error;
    }
  }

  // User preferences operations
  async addUserPreference(userId, movie) {
    if (Platform.OS === 'web') {
      const response = await fetch(`${API_URL}/preferences`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, movie }),
      });
      return response.json();
    }

    try {
      const client = await this.getConnection();
      const result = await client.query(
        'INSERT INTO user_preferences (user_id, movie_id, title, image, description, rating) VALUES ($1, $2, $3, $4, $5, $6) RETURNING id',
        [userId, movie.id, movie.title, movie.image, movie.description, movie.rating]
      );
      await client.end();
      return result.rows[0].id;
    } catch (error) {
      console.error('Error adding user preference:', error);
      throw error;
    }
  }

  async getUserPreferences(userId) {
    if (Platform.OS === 'web') {
      const response = await fetch(`${API_URL}/preferences/${userId}`);
      return response.json();
    }

    try {
      const client = await this.getConnection();
      const result = await client.query(
        'SELECT * FROM user_preferences WHERE user_id = $1',
        [userId]
      );
      await client.end();
      return result.rows;
    } catch (error) {
      console.error('Error getting user preferences:', error);
      throw error;
    }
  }
}

export default new DatabaseService();
