import React, { createContext, useState, useContext } from 'react';
import database from '../services/database';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);

  const login = async (email, password) => {
    try {
      setLoading(true);
      const userData = await database.getUser(email, password);
      if (userData) {
        setUser(userData);
        return true;
      }
      return false;
    } catch (error) {
      console.error('Login error:', error);
      return false;
    } finally {
      setLoading(false);
    }
  };

  const register = async (email, password) => {
    try {
      setLoading(true);
      const userId = await database.createUser(email, password);
      if (userId) {
        const userData = await database.getUser(email, password);
        setUser(userData);
        return true;
      }
      return false;
    } catch (error) {
      console.error('Registration error:', error);
      return false;
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    setUser(null);
  };

  const updateUserPreferences = async (movie) => {
    if (!user) return;
    try {
      await database.addUserPreference(user.id, movie);
    } catch (error) {
      console.error('Error updating preferences:', error);
    }
  };

  const updateWatchlist = async (movie) => {
    if (!user) return;
    try {
      await database.addToWatchlist(user.id, movie);
    } catch (error) {
      console.error('Error updating watchlist:', error);
    }
  };

  const getWatchlist = async () => {
    if (!user) return [];
    try {
      return await database.getWatchlist(user.id);
    } catch (error) {
      console.error('Error getting watchlist:', error);
      return [];
    }
  };

  const getUserPreferences = async () => {
    if (!user) return [];
    try {
      return await database.getUserPreferences(user.id);
    } catch (error) {
      console.error('Error getting preferences:', error);
      return [];
    }
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        loading,
        login,
        register,
        logout,
        updateUserPreferences,
        updateWatchlist,
        getWatchlist,
        getUserPreferences,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
