import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Dimensions,
  Animated,
  PanResponder,
  Image,
  Platform,
  TouchableOpacity,
  Modal,
} from 'react-native';
import { useAuth } from '../context/AuthContext';
import SwipeRecommendations from './SwipeRecommendations';

const SCREEN_WIDTH = Dimensions.get('window').width;
const SWIPE_THRESHOLD = 0.25 * SCREEN_WIDTH;

export default function HomeScreen() {
  const [movies, setMovies] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const position = new Animated.ValueXY();
  const { updateUserPreferences, updateWatchlist } = useAuth();
  const [showRecommendations, setShowRecommendations] = useState(false);

  useEffect(() => {
    // Using TMDB API to fetch popular movies
    const fetchMovies = async () => {
      try {
        const response = await fetch(
          'https://api.themoviedb.org/3/movie/popular?api_key=1f54bd990f1c42b8c92a9e4e3ac23cb7&language=en-US&page=1'
        );
        const data = await response.json();

        const formattedMovies = data.results.map(movie => ({
          id: movie.id,
          title: movie.title,
          image: `https://image.tmdb.org/t/p/w500${movie.poster_path}`,
          description: movie.overview,
          rating: movie.vote_average,
        }));

        setMovies(formattedMovies);
      } catch (error) {
        console.error('Error fetching movies:', error);
        // Fallback data in case of API error
        setMovies([
          {
            id: 1,
            title: 'Inception',
            image: 'https://image.tmdb.org/t/p/w500/9gk7adHYeDvHkCSEqAvQNLV5Uge.jpg',
            description: 'A thief who steals corporate secrets through dream-sharing technology.',
            rating: 8.8,
          },
          {
            id: 2,
            title: 'The Matrix',
            image: 'https://image.tmdb.org/t/p/w500/f89U3ADr1oiB1s9GkdPOEpXUk5H.jpg',
            description: 'A computer programmer discovers a mysterious world.',
            rating: 8.7,
          },
          {
            id: 3,
            title: 'The Dark Knight',
            image: 'https://image.tmdb.org/t/p/w500/qJ2tW6WMUDux911r6m7haRef0WH.jpg',
            description: 'When the menace known as the Joker wreaks havoc and chaos on the people of Gotham.',
            rating: 8.9,
          }
        ]);
      }
    };

    fetchMovies();

    // Add keyboard event listeners for web
    if (Platform.OS === 'web') {
      const handleKeyPress = (event) => {
        if (event.key === 'ArrowRight') {
          forceSwipe('right');
        } else if (event.key === 'ArrowLeft') {
          forceSwipe('left');
        }
      };

      window.addEventListener('keydown', handleKeyPress);
      return () => window.removeEventListener('keydown', handleKeyPress);
    }
  }, []);

  const panResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,
    onPanResponderMove: (event, gesture) => {
      position.setValue({ x: gesture.dx, y: gesture.dy });
    },
    onPanResponderRelease: (event, gesture) => {
      if (gesture.dx > SWIPE_THRESHOLD) {
        forceSwipe('right');
      } else if (gesture.dx < -SWIPE_THRESHOLD) {
        forceSwipe('left');
      } else {
        resetPosition();
      }
    },
  });

  const forceSwipe = (direction) => {
    const x = direction === 'right' ? SCREEN_WIDTH : -SCREEN_WIDTH;
    Animated.timing(position, {
      toValue: { x, y: 0 },
      duration: 250,
      useNativeDriver: false,
    }).start(() => onSwipeComplete(direction));
  };

  const onSwipeComplete = (direction) => {
    const item = movies[currentIndex];
    direction === 'right' ? updateWatchlist(item) : updateUserPreferences(item);
    position.setValue({ x: 0, y: 0 });
    setCurrentIndex(currentIndex + 1);
  };

  const resetPosition = () => {
    Animated.spring(position, {
      toValue: { x: 0, y: 0 },
      useNativeDriver: false,
    }).start();
  };

  const getCardStyle = () => {
    const rotate = position.x.interpolate({
      inputRange: [-SCREEN_WIDTH * 1.5, 0, SCREEN_WIDTH * 1.5],
      outputRange: ['-120deg', '0deg', '120deg'],
    });

    return {
      ...position.getLayout(),
      transform: [{ rotate }],
    };
  };

  if (currentIndex >= movies.length) {
    return (
      <View style={styles.noMoreCards}>
        <Text style={styles.noMoreCardsText}>No more recommendations!</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>StreamFlix</Text>
        <TouchableOpacity
          style={styles.recommendButton}
          onPress={() => setShowRecommendations(true)}
        >
          <Text style={styles.recommendButtonText}>Show me recommendations</Text>
        </TouchableOpacity>
      </View>

      <Modal
        visible={showRecommendations}
        animationType="slide"
        transparent={true}
        onRequestClose={() => setShowRecommendations(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <TouchableOpacity
              style={styles.closeButton}
              onPress={() => setShowRecommendations(false)}
            >
              <Text style={styles.closeButtonText}>Ã—</Text>
            </TouchableOpacity>
            <SwipeRecommendations onClose={() => setShowRecommendations(false)} />
          </View>
        </View>
      </Modal>

      {Platform.OS === 'web' && (
        <View style={styles.webControls}>
          <TouchableOpacity
            style={[styles.webButton, styles.rejectButton]}
            onPress={() => forceSwipe('left')}
          >
            <Text style={styles.webButtonText}>Skip</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={[styles.webButton, styles.acceptButton]}
            onPress={() => forceSwipe('right')}
          >
            <Text style={styles.webButtonText}>Add to Watchlist</Text>
          </TouchableOpacity>
        </View>
      )}
      {movies.map((movie, index) => {
        if (index < currentIndex) return null;

        if (index === currentIndex) {
          return (
            <Animated.View
              key={movie.id}
              style={[getCardStyle(), styles.cardStyle]}
              {...panResponder.panHandlers}
            >
              <Image
                source={{ uri: movie.image }}
                style={styles.image}
                resizeMode="cover"
              />
              <View style={styles.cardContent}>
                <Text style={styles.title}>{movie.title}</Text>
                <Text style={styles.rating}>Rating: {movie.rating}/10</Text>
                <Text style={styles.description}>{movie.description}</Text>
              </View>
            </Animated.View>
          );
        }

        return (
          <View
            key={movie.id}
            style={[styles.cardStyle, { top: 10 * (index - currentIndex) }]}
          >
            <Image
              source={{ uri: movie.image }}
              style={styles.image}
              resizeMode="cover"
            />
            <View style={styles.cardContent}>
              <Text style={styles.title}>{movie.title}</Text>
              <Text style={styles.rating}>Rating: {movie.rating}/10</Text>
              <Text style={styles.description}>{movie.description}</Text>
            </View>
          </View>
        );
      }).reverse()}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#141414',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
