import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Dimensions,
  Animated,
  PanResponder,
  Image,
  Platform,
  TouchableOpacity,
} from 'react-native';
import { useAuth } from '../context/AuthContext';

const { width } = Dimensions.get('window');
const CARD_WIDTH = width * 0.4;
const CARD_HEIGHT = CARD_WIDTH * 1.5;
const SWIPE_THRESHOLD = 0.25 * CARD_WIDTH;

export default function SwipeRecommendations({ onClose }) {
  const [movies, setMovies] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const position = new Animated.ValueXY();
  const { updateUserPreferences, updateWatchlist } = useAuth();

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch(
          'https://api.themoviedb.org/3/movie/popular?api_key=1f54bd990f1c42b8c92a9e4e3ac23cb7&language=en-US&page=1'
        );
        const data = await response.json();

        const formattedMovies = data.results.map(movie => ({
          id: movie.id,
          title: movie.title,
          image: `https://image.tmdb.org/t/p/w500${movie.poster_path}`,
          description: movie.overview,
          rating: movie.vote_average,
        }));

        setMovies(formattedMovies);
      } catch (error) {
        console.error('Error fetching movies:', error);
        setMovies([
          {
            id: 1,
            title: 'Inception',
            image: 'https://image.tmdb.org/t/p/w500/9gk7adHYeDvHkCSEqAvQNLV5Uge.jpg',
            description: 'A thief who steals corporate secrets through dream-sharing technology.',
            rating: 8.8,
          },
          {
            id: 2,
            title: 'The Matrix',
            image: 'https://image.tmdb.org/t/p/w500/f89U3ADr1oiB1s9GkdPOEpXUk5H.jpg',
            description: 'A computer programmer discovers a mysterious world.',
            rating: 8.7,
          },
          {
            id: 3,
            title: 'The Dark Knight',
            image: 'https://image.tmdb.org/t/p/w500/qJ2tW6WMUDux911r6m7haRef0WH.jpg',
            description: 'When the menace known as the Joker wreaks havoc and chaos on the people of Gotham.',
            rating: 8.9,
          }
        ]);
      } finally {
        setIsLoading(false);
      }
    };

    fetchMovies();

    if (Platform.OS === 'web') {
      const handleKeyPress = (event) => {
        if (event.key === 'ArrowRight') {
          forceSwipe('right');
        } else if (event.key === 'ArrowLeft') {
          forceSwipe('left');
        } else if (event.key === 'Escape') {
          onClose();
        }
      };

      window.addEventListener('keydown', handleKeyPress);
      return () => window.removeEventListener('keydown', handleKeyPress);
    }
  }, [onClose]);

  const panResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,
    onPanResponderMove: (event, gesture) => {
      position.setValue({ x: gesture.dx, y: gesture.dy });
    },
    onPanResponderRelease: (event, gesture) => {
      if (gesture.dx > SWIPE_THRESHOLD) {
        forceSwipe('right');
      } else if (gesture.dx < -SWIPE_THRESHOLD) {
        forceSwipe('left');
      } else {
        resetPosition();
      }
    },
  });

  const forceSwipe = (direction) => {
    const x = direction === 'right' ? width : -width;
    Animated.timing(position, {
      toValue: { x, y: 0 },
      duration: 250,
      useNativeDriver: false,
    }).start(() => onSwipeComplete(direction));
  };

  const onSwipeComplete = (direction) => {
    const item = movies[currentIndex];
    direction === 'right' ? updateWatchlist(item) : updateUserPreferences(item);
    position.setValue({ x: 0, y: 0 });

    if (currentIndex < movies.length - 1) {
      setCurrentIndex(currentIndex + 1);
    } else {
      // Reset to the beginning when we reach the end
      setCurrentIndex(0);
      // Shuffle the movies array to show them in a different order
      setMovies(prevMovies => {
        const shuffled = [...prevMovies];
        for (let i = shuffled.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
        }
        return shuffled;
      });
    }
  };

  const resetPosition = () => {
    Animated.spring(position, {
      toValue: { x: 0, y: 0 },
      useNativeDriver: false,
    }).start();
  };

  const getCardStyle = () => {
    const rotate = position.x.interpolate({
      inputRange: [-width * 1.5, 0, width * 1.5],
      outputRange: ['-30deg', '0deg', '30deg'],
    });

    return {
      ...position.getLayout(),
      transform: [{ rotate }],
      position: 'absolute',
      width: CARD_WIDTH,
      height: CARD_HEIGHT,
      borderRadius: 12,
      backgroundColor: '#fff',
      shadowColor: '#000',
      shadowOpacity: 0.2,
      shadowRadius: 5,
      shadowOffset: { width: 0, height: 2 },
      elevation: 5,
      left: (width - CARD_WIDTH) / 2,
    };
  };

  if (isLoading) {
    return (
      <View style={styles.container}>
        <Text style={styles.loadingText}>Loading recommendations...</Text>
      </View>
    );
  }

  if (movies.length === 0) {
    return (
      <View style={styles.container}>
        <Text style={styles.noMoreCardsText}>No movies available!</Text>
        <TouchableOpacity style={styles.closeButton} onPress={onClose}>
          <Text style={styles.closeButtonText}>Close</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.controls}>
        <TouchableOpacity
          style={[styles.controlButton, styles.rejectButton]}
          onPress={() => forceSwipe('left')}
        >
          <Text style={styles.controlButtonText}>Skip</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.controlButton, styles.acceptButton]}
          onPress={() => forceSwipe('right')}
        >
          <Text style={styles.controlButtonText}>Add to Watchlist</Text>
        </TouchableOpacity>
      </View>
      {movies.map((movie, index) => {
        if (index < currentIndex) return null;

        if (index === currentIndex) {
          return (
            <Animated.View
              key={movie.id}
              style={[getCardStyle(), styles.cardStyle]}
              {...panResponder.panHandlers}
            >
              <Image
                source={{ uri: movie.image }}
                style={styles.image}
                resizeMode="cover"
              />
              <View style={styles.cardContent}>
                <Text style={styles.title}>{movie.title}</Text>
                <Text style={styles.rating}>Rating: {movie.rating}/10</Text>
                <Text style={styles.description} numberOfLines={3}>
                  {movie.description}
                </Text>
              </View>
            </Animated.View>
          );
        }

        return (
          <View
            key={movie.id}
            style={[styles.cardStyle, { top: 5 * (index - currentIndex) }]}
          >
            <Image
              source={{ uri: movie.image }}
              style={styles.image}
              resizeMode="cover"
            />
            <View style={styles.cardContent}>
              <Text style={styles.title}>{movie.title}</Text>
              <Text style={styles.rating}>Rating: {movie.rating}/10</Text>
              <Text style={styles.description} numberOfLines={3}>
                {movie.description}
              </Text>
            </View>
          </View>
        );
      }).reverse()}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#141414',
    width: '100%',
  },
  cardStyle: {
    position: 'absolute',
    width: CARD_WIDTH,
    height: CARD_HEIGHT,
    borderRadius: 12,
    backgroundColor: '#fff',
    shadowColor: '#000',
    shadowOpacity: 0.2,
    shadowRadius: 5,
    shadowOffset: { width: 0, height: 2 },
    elevation: 5,
    left: (width - CARD_WIDTH) / 2,
  },
  image: {
    width: '100%',
    height: '60%',
    borderTopLeftRadius: 12,
    borderTopRightRadius: 12,
  },
  cardContent: {
    padding: 10,
  },
  title: {
    fontSize: 14,
    fontWeight: 'bold',
    marginBottom: 3,
  },
  rating: {
    fontSize: 11,
    color: '#666',
    marginBottom: 3,
  },
  description: {
    fontSize: 10,
    color: '#444',
